import React, { useState } from "react";
import PropTypes from "prop-types";
import { NavLink } from "react-router-dom";
import {
  Collapse,
  Navbar,
  NavbarToggler,
  NavbarBrand,
  Nav,
  NavItem,
} from "reactstrap";
import "./Header.scss";

const Header = (props) => {
  const [searchContext, setSearchContext] = useState("");

  const [isOpen, setIsOpen] = useState(false);
  const toggle = () => {
    setIsOpen(!isOpen);
  };
  return (
    <div>
      <Navbar color="light" light expand="md">
        <NavbarBrand href="/">
          <div className="d-flex justify-content-space align-items-center">
            <img className="height-navbar" src="/diamond.png" />
            <span className="d-none d-md-block">DiamondShop</span>
          </div>
        </NavbarBrand>
        <NavbarToggler onClick={toggle} />
        <Collapse className="collapse" isOpen={isOpen} navbar>
          <Nav navbar>
            <NavItem>
              <NavLink
                onClick={toggle}
                activeClassName="nav-item--active"
                to="/clothes"
              >
                Áo quần
              </NavLink>
            </NavItem>
            <NavItem>
              <NavLink activeClassName="nav-item--active" to="/shoes">
                Giày
              </NavLink>
            </NavItem>
            <NavItem>
              <NavLink activeClassName="nav-item--active" to="/accessories">
                Phụ kiện
              </NavLink>
            </NavItem>
          </Nav>
        </Collapse>
      </Navbar>
    </div>
  );
};

Header.propTypes = {};

export default Header;
