import React, { useState } from "react";
import PropTypes from "prop-types";
import {
  Nav,
  Navbar,
  NavbarBrand,
  Collapse,
  NavbarToggler,
  NavItem,
} from "reactstrap";
import { NavLink } from "react-router-dom";

import "./Header.scss";

const Header = (props) => {
  const [searchContext, setSearchContext] = useState("");
  const [isOpen, setIsOpen] = useState(false);
  const [iconClassName, setIcon] = useState();

  const toggle = () => {
    setIsOpen(!isOpen);
  };
  return (
    <div className="header">
      <Navbar color="light" light expand="md">
        <NavbarBrand>
          <div className="d-flex align-items-center">
            <img className="navbar-height" src="/diamond.png" />
            <span className="ml-3 bold d-none d-md-inline-block">
              DiamondShop
            </span>
          </div>
        </NavbarBrand>
        <NavbarToggler onClick={toggle} />
        <Collapse isOpen={isOpen} navbar>
          <Nav className="mt-2 mt-md-0" navbar>
            <NavItem>
              <NavLink to="/clothes">Quần áo</NavLink>
            </NavItem>
            <NavItem>
              <NavLink to="/shoes">Giày</NavLink>
            </NavItem>
            <NavItem>
              <NavLink to="/accessories">Phụ kiện</NavLink>
            </NavItem>
          </Nav>
        </Collapse>
      </Navbar>
    </div>
  );
};

Header.propTypes = {};

export default Header;
